"use strict";

function switchPannels() {
  var t = document.querySelector(".buttons").querySelectorAll(".buttons__screen"),
      e = document.querySelectorAll(".buttons__btn[data-switchto]"),
      s = document.querySelectorAll("a.buttons__btn");

  function n() {
    t.forEach(function (t) {
      t.style.height = 0, setTimeout(function () {
        t.classList.add("--invisible"), t.removeAttribute("style");
      }, 250);
    });
  }

  function c(t) {
    var e = document.querySelector(".buttons__screen[data-screen=\"".concat(t.target.dataset.switchto, "\"]"));
    n(), setTimeout(function () {
      e.style.height = e.scrollHeight + "px", setTimeout(function () {
        e.classList.remove("--invisible"), e.removeAttribute("style");
      }, 250);
    }, 250);
  }

  e.forEach(function (t) {
    return t.addEventListener("click", c);
  }), s.forEach(function (t) {
    return t.addEventListener("click", n);
  });
}

switchPannels();
"use strict";